import { ADD_FAV, REMOVE_FAV } from "./action"

const inicialState={
    myFav:[],
}

const rootReducer=(state=inicialState, { type, payload })=>{
    switch(type){
        case ADD_FAV:
            return{
                ...state,
                myFav: [...state.myFav, payload]
            }
        case REMOVE_FAV:
            return{
                myFav:state.myFav.filter((ch)=>ch!=payload)
            }
        default:
            return{
                ...state
            }

    }
}

export default rootReducer;
